content.content:
     1→/* Modern CSS Reset */
     2→*, *::before, *::after {
     3→    box-sizing: border-box;
     4→    margin: 0;
     5→    padding: 0;
     6→}
     7→
     8→/* CSS Variables */
     9→:root {
    10→    --primary-color: #2563eb;
    11→    --primary-hover: #1d4ed8;
    12→    --background: #0f172a;
    13→    --surface: #1e293b;
    14→    --surface-hover: #334155;
    15→    --text-primary: #f1f5f9;
    16→    --text-secondary: #94a3b8;
    17→    --border-color: #334155;
    18→    --user-message: #2563eb;
    19→    --assistant-message: #374151;
    20→    --shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.3);
    21→    --radius: 12px;
    22→    --focus-ring: rgba(37, 99, 235, 0.2);
    23→    --welcome-bg: #1e3a5f;
    24→    --welcome-border: #2563eb;
    25→}
    26→
    27→/* Base Styles */
    28→body {
    29→    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
    30→    background-color: var(--background);
    31→    color: var(--text-primary);
    32→    line-height: 1.6;
    33→    height: 100vh;
    34→    overflow: hidden;
    35→    margin: 0;
    36→    padding: 0;
    37→}
    38→
    39→/* Container - Full Screen */
    40→.container {
    41→    height: 100vh;
    42→    width: 100vw;
    43→    display: flex;
    44→    flex-direction: column;
    45→    margin: 0;
    46→    padding: 0;
    47→}
    48→
    49→/* Header - Hidden */
    50→header {
    51→    display: none;
    52→}
    53→
    54→header h1 {
    55→    font-size: 1.75rem;
    56→    font-weight: 700;
    57→    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    58→    -webkit-background-clip: text;
    59→    -webkit-text-fill-color: transparent;
    60→    background-clip: text;
    61→    margin: 0;
    62→}
    63→
    64→.subtitle {
    65→    font-size: 0.95rem;
    66→    color: var(--text-secondary);
    67→    margin-top: 0.5rem;
    68→}
    69→
    70→/* Main Content Area with Sidebar */
    71→.main-content {
    72→    flex: 1;
    73→    display: flex;
    74→    overflow: hidden;
    75→    background: var(--background);
    76→}
    77→
    78→/* Left Sidebar */
    79→.sidebar {
    80→    width: 320px;
    81→    background: var(--surface);
    82→    border-right: 1px solid var(--border-color);
    83→    padding: 1.5rem;
    84→    overflow-y: auto;
    85→    flex-shrink: 0;
    86→}
    87→
    88→/* Custom Scrollbar for Sidebar */
    89→.sidebar::-webkit-scrollbar {
    90→    width: 8px;
    91→}
    92→
    93→.sidebar::-webkit-scrollbar-track {
    94→    background: var(--surface);
    95→}
    96→
    97→.sidebar::-webkit-scrollbar-thumb {
    98→    background: var(--border-color);
    99→    border-radius: 4px;
   100→}
   101→
   102→.sidebar::-webkit-scrollbar-thumb:hover {
   103→    background: var(--text-secondary);
   104→}
   105→
   106→.sidebar-section {
   107→    margin-bottom: 1.5rem;
   108→}
   109→
   110→.sidebar-section:last-child {
   111→    margin-bottom: 0;
   112→}
   113→
   114→/* Main Chat Area */
   115→.chat-main {
   116→    flex: 1;
   117→    display: flex;
   118→    justify-content: center;
   119→    overflow: hidden;
   120→    padding: 0;
   121→    background: var(--background);
   122→}
   123→
   124→/* Chat Container - Centered with Max Width */
   125→.chat-container {
   126→    flex: 1;
   127→    display: flex;
   128→    flex-direction: column;
   129→    background: var(--background);
   130→    overflow: hidden;
   131→    width: 100%;
   132→    max-width: 800px;
   133→    margin: 0;
   134→}
   135→
   136→/* Chat Messages */
   137→.chat-messages {
   138→    flex: 1;
   139→    overflow-y: auto;
   140→    padding: 2rem;
   141→    display: flex;
   142→    flex-direction: column;
   143→    gap: 1rem;
   144→    background: var(--background);
   145→}
   146→
   147→/* Custom Scrollbar */
   148→.chat-messages::-webkit-scrollbar {
   149→    width: 8px;
   150→}
   151→
   152→.chat-messages::-webkit-scrollbar-track {
   153→    background: var(--surface);
   154→}
   155→
   156→.chat-messages::-webkit-scrollbar-thumb {
   157→    background: var(--border-color);
   158→    border-radius: 4px;
   159→}
   160→
   161→.chat-messages::-webkit-scrollbar-thumb:hover {
   162→    background: var(--text-secondary);
   163→}
   164→
   165→/* Message Styles */
   166→.message {
   167→    max-width: 85%;
   168→    animation: fadeIn 0.3s ease-out;
   169→}
   170→
   171→@keyframes fadeIn {
   172→    from {
   173→        opacity: 0;
   174→        transform: translateY(10px);
   175→    }
   176→    to {
   177→        opacity: 1;
   178→        transform: translateY(0);
   179→    }
   180→}
   181→
   182→.message.user {
   183→    align-self: flex-end;
   184→}
   185→
   186→.message.assistant {
   187→    align-self: flex-start;
   188→}
   189→
   190→.message-content {
   191→    padding: 0.75rem 1.25rem;
   192→    border-radius: 18px;
   193→    word-wrap: break-word;
   194→    line-height: 1.5;
   195→}
   196→
   197→.message.user .message-content {
   198→    background: var(--user-message);
   199→    color: white;
   200→    border-bottom-right-radius: 4px;
   201→}
   202→
   203→.message.assistant .message-content {
   204→    background: var(--surface);
   205→    color: var(--text-primary);
   206→    border-bottom-left-radius: 4px;
   207→}
   208→
   209→/* Message metadata */
   210→.message-meta {
   211→    font-size: 0.75rem;
   212→    color: var(--text-secondary);
   213→    margin-top: 0.25rem;
   214→    padding: 0 0.5rem;
   215→}
   216→
   217→.message.user .message-meta {
   218→    text-align: right;
   219→}
   220→
   221→/* Collapsible Sources */
   222→.sources-collapsible {
   223→    margin-top: 0.5rem;
   224→    font-size: 0.75rem;
   225→    color: var(--text-secondary);
   226→}
   227→
   228→.sources-collapsible summary {
   229→    cursor: pointer;
   230→    padding: 0.25rem 0.5rem;
   231→    user-select: none;
   232→    font-weight: 500;
   233→}
   234→
   235→.sources-collapsible summary:hover {
   236→    color: var(--text-primary);
   237→}
   238→
   239→.sources-collapsible[open] summary {
   240→    margin-bottom: 0.25rem;
   241→}
   242→
   243→.sources-content {
   244→    padding: 0 0.5rem 0.25rem 1.5rem;
   245→    color: var(--text-secondary);
   246→}
   247→
   248→.sources-content a {
   249→    color: #93c5fd !important;
   250→    text-decoration: none;
   251→    border: 2px solid #60a5fa !important;
   252→    border-radius: 6px;
   253→    padding: 4px 8px;
   254→    background-color: rgba(96, 165, 250, 0.15) !important;
   255→    display: inline-block;
   256→    margin: 0 2px;
   257→    font-weight: 500;
   258→    transition: all 0.2s ease;
   259→}
   260→
   261→.sources-content a:hover {
   262→    color: #dbeafe !important;
   263→    border-color: #93c5fd !important;
   264→    background-color: rgba(96, 165, 250, 0.3) !important;
   265→    text-shadow: 0 0 12px rgba(96, 165, 250, 0.5);
   266→    transform: translateY(-1px);
   267→}
   268→
   269→.sources-content a:focus {
   270→    outline: 2px solid #60a5fa;
   271→    outline-offset: 2px;
   272→    border-radius: 2px;
   273→}
   274→
   275→/* Markdown formatting styles */
   276→.message-content h1,
   277→.message-content h2,
   278→.message-content h3,
   279→.message-content h4,
   280→.message-content h5,
   281→.message-content h6 {
   282→    margin: 0.5rem 0;
   283→    font-weight: 600;
   284→}
   285→
   286→.message-content h1 { font-size: 1.5rem; }
   287→.message-content h2 { font-size: 1.3rem; }
   288→.message-content h3 { font-size: 1.1rem; }
   289→
   290→.message-content p {
   291→    margin: 0.5rem 0;
   292→    line-height: 1.6;
   293→}
   294→
   295→.message-content ul,
   296→.message-content ol {
   297→    margin: 0.5rem 0;
   298→    padding-left: 1.5rem;
   299→}
   300→
   301→.message-content li {
   302→    margin: 0.25rem 0;
   303→    line-height: 1.6;
   304→}
   305→
   306→.message-content code {
   307→    background-color: rgba(0, 0, 0, 0.2);
   308→    padding: 0.125rem 0.25rem;
   309→    border-radius: 3px;
   310→    font-family: 'Fira Code', 'Consolas', monospace;
   311→    font-size: 0.875em;
   312→}
   313→
   314→.message-content pre {
   315→    background-color: rgba(0, 0, 0, 0.2);
   316→    padding: 0.75rem;
   317→    border-radius: 4px;
   318→    overflow-x: auto;
   319→    margin: 0.5rem 0;
   320→}
   321→
   322→.message-content pre code {
   323→    background-color: transparent;
   324→    padding: 0;
   325→}
   326→
   327→.message-content blockquote {
   328→    border-left: 3px solid var(--primary);
   329→    padding-left: 1rem;
   330→    margin: 0.5rem 0;
   331→    color: var(--text-secondary);
   332→}
   333→
   334→/* Welcome message special styling */
   335→.message.welcome-message .message-content {
   336→    background: var(--surface);
   337→    border: 2px solid var(--border-color);
   338→    box-shadow: 0 4px 16px rgba(0, 0, 0, 0.2);
   339→    position: relative;
   340→}
   341→
   342→.message-content strong {
   343→    font-weight: 600;
   344→}
   345→
   346→.message-content em {
   347→    font-style: italic;
   348→}
   349→
   350→.message-content hr {
   351→    border: none;
   352→    border-top: 1px solid var(--border-color);
   353→    margin: 1rem 0;
   354→}
   355→
   356→/* Chat Input Container */
   357→.chat-input-container {
   358→    display: flex;
   359→    gap: 0.75rem;
   360→    padding: 1.5rem 2rem;
   361→    background: var(--background);
   362→    border-top: 1px solid var(--border-color);
   363→    flex-shrink: 0;
   364→}
   365→
   366→/* Chat Input */
   367→#chatInput {
   368→    flex: 1;
   369→    padding: 0.875rem 1.25rem;
   370→    background: var(--surface);
   371→    border: 1px solid var(--border-color);
   372→    border-radius: 24px;
   373→    color: var(--text-primary);
   374→    font-size: 0.95rem;
   375→    transition: all 0.2s ease;
   376→}
   377→
   378→#chatInput:focus {
   379→    outline: none;
   380→    border-color: var(--primary-color);
   381→    box-shadow: 0 0 0 3px var(--focus-ring);
   382→}
   383→
   384→#chatInput::placeholder {
   385→    color: var(--text-secondary);
   386→}
   387→
   388→/* Send Button */
   389→#sendButton {
   390→    padding: 0.75rem 1.25rem;
   391→    background: var(--primary-color);
   392→    color: white;
   393→    border: none;
   394→    border-radius: 24px;
   395→    cursor: pointer;
   396→    display: flex;
   397→    align-items: center;
   398→    justify-content: center;
   399→    transition: all 0.2s ease;
   400→    min-width: 52px;
   401→}
   402→
   403→#sendButton:focus {
   404→    outline: none;
   405→    box-shadow: 0 0 0 3px var(--focus-ring);
   406→}
   407→
   408→#sendButton:hover:not(:disabled) {
   409→    background: var(--primary-hover);
   410→    transform: translateY(-1px);
   411→    box-shadow: 0 4px 12px rgba(37, 99, 235, 0.3);
   412→}
   413→
   414→#sendButton:active:not(:disabled) {
   415→    transform: translateY(0);
   416→}
   417→
   418→#sendButton:disabled {
   419→    opacity: 0.5;
   420→    cursor: not-allowed;
   421→}
   422→
   423→/* Loading Animation */
   424→.loading {
   425→    display: inline-flex;
   426→    gap: 4px;
   427→    padding: 0.75rem 1.25rem;
   428→}
   429→
   430→.loading span {
   431→    width: 8px;
   432→    height: 8px;
   433→    background: var(--text-secondary);
   434→    border-radius: 50%;
   435→    animation: bounce 1.4s infinite ease-in-out both;
   436→}
   437→
   438→.loading span:nth-child(1) {
   439→    animation-delay: -0.32s;
   440→}
   441→
   442→.loading span:nth-child(2) {
   443→    animation-delay: -0.16s;
   444→}
   445→
   446→@keyframes bounce {
   447→    0%, 80%, 100% {
   448→        transform: scale(0);
   449→    }
   450→    40% {
   451→        transform: scale(1);
   452→    }
   453→}
   454→
   455→/* Error Message */
   456→.error-message {
   457→    background: rgba(239, 68, 68, 0.1);
   458→    color: #f87171;
   459→    padding: 0.75rem 1.25rem;
   460→    border-radius: 8px;
   461→    border: 1px solid rgba(239, 68, 68, 0.2);
   462→    margin: 0.5rem 0;
   463→}
   464→
   465→/* Success Message */
   466→.success-message {
   467→    background: rgba(34, 197, 94, 0.1);
   468→    color: #4ade80;
   469→    padding: 0.75rem 1.25rem;
   470→    border-radius: 8px;
   471→    border: 1px solid rgba(34, 197, 94, 0.2);
   472→    margin: 0.5rem 0;
   473→}
   474→
   475→/* Sidebar Headers */
   476→.stats-header,
   477→.suggested-header {
   478→    font-size: 0.875rem;
   479→    font-weight: 600;
   480→    color: var(--text-secondary);
   481→    cursor: pointer;
   482→    padding: 0.5rem 0;
   483→    border: none;
   484→    background: none;
   485→    list-style: none;
   486→    outline: none;
   487→    transition: color 0.2s ease;
   488→    text-transform: uppercase;
   489→    letter-spacing: 0.5px;
   490→}
   491→
   492→.stats-header:focus,
   493→.suggested-header:focus {
   494→    color: var(--primary-color);
   495→}
   496→
   497→.stats-header:hover,
   498→.suggested-header:hover {
   499→    color: var(--primary-color);
   500→}
   501→
   502→.stats-header::-webkit-details-marker,
   503→.suggested-header::-webkit-details-marker {
   504→    display: none;
   505→}
   506→
   507→.stats-header::before,
   508→.suggested-header::before {
   509→    content: '▶';
   510→    display: inline-block;
   511→    margin-right: 0.5rem;
   512→    transition: transform 0.2s ease;
   513→    font-size: 0.75rem;
   514→}
   515→
   516→details[open] .stats-header::before,
   517→details[open] .suggested-header::before {
   518→    transform: rotate(90deg);
   519→}
   520→
   521→/* Course Stats in Sidebar */
   522→.course-stats {
   523→    display: flex;
   524→    flex-direction: column;
   525→    gap: 1rem;
   526→    padding: 0.75rem 0;
   527→    background: transparent;
   528→    border: none;
   529→}
   530→
   531→.stat-item {
   532→    text-align: left;
   533→    padding: 0.75rem;
   534→    background: var(--background);
   535→    border-radius: 8px;
   536→    border: 1px solid var(--border-color);
   537→    margin-bottom: 0.75rem;
   538→}
   539→
   540→.stat-item:last-child {
   541→    margin-bottom: 0;
   542→}
   543→
   544→.stat-value {
   545→    display: inline-block;
   546→    font-size: 0.875rem;
   547→    font-weight: 600;
   548→    color: var(--primary-color);
   549→    margin-left: 0.5rem;
   550→}
   551→
   552→.stat-label {
   553→    display: inline-block;
   554→    font-size: 0.875rem;
   555→    color: var(--text-secondary);
   556→    font-weight: 600;
   557→}
   558→
   559→.stat-item:last-child .stat-label {
   560→    display: block;
   561→    margin-bottom: 0.5rem;
   562→}
   563→
   564→/* Course titles collapsible */
   565→.course-titles-collapsible {
   566→    width: 100%;
   567→}
   568→
   569→.course-titles-header {
   570→    cursor: pointer;
   571→    font-size: 0.875rem;
   572→    color: var(--text-secondary);
   573→    font-weight: 600;
   574→    padding: 0.5rem 0;
   575→    list-style: none;
   576→    display: block;
   577→    user-select: none;
   578→}
   579→
   580→.course-titles-header:focus {
   581→    outline: none;
   582→    color: var(--primary-color);
   583→}
   584→
   585→.course-titles-header::-webkit-details-marker {
   586→    display: none;
   587→}
   588→
   589→.course-titles-header::before {
   590→    content: '▶';
   591→    display: inline-block;
   592→    margin-right: 0.5rem;
   593→    transition: transform 0.2s ease;
   594→    font-size: 0.75rem;
   595→}
   596→
   597→.course-titles-collapsible[open] .course-titles-header::before {
   598→    transform: rotate(90deg);
   599→}
   600→
   601→/* Course titles display */
   602→.course-titles {
   603→    margin-top: 0.5rem;
   604→    /* Remove max-height to show all titles without scrolling */
   605→}
   606→
   607→.course-title-item {
   608→    font-size: 0.85rem;
   609→    color: var(--text-primary);
   610→    padding: 0.5rem 0.25rem;
   611→    border-bottom: 1px solid var(--border-color);
   612→    text-transform: none;
   613→    line-height: 1.4;
   614→}
   615→
   616→.course-title-item:last-child {
   617→    border-bottom: none;
   618→}
   619→
   620→.course-title-item:first-child {
   621→    padding-top: 0.25rem;
   622→}
   623→
   624→.no-courses, .loading, .error {
   625→    font-size: 0.85rem;
   626→    color: var(--text-secondary);
   627→    font-style: italic;
   628→    text-transform: none;
   629→}
   630→
   631→/* Suggested Questions in Sidebar */
   632→.suggested-items {
   633→    display: flex;
   634→    flex-direction: column;
   635→    gap: 0.5rem;
   636→    padding: 0.75rem 0;
   637→}
   638→
   639→.suggested-item {
   640→    padding: 0.75rem 1rem;
   641→    background: var(--background);
   642→    border: 1px solid var(--border-color);
   643→    border-radius: 8px;
   644→    color: var(--text-primary);
   645→    font-size: 0.875rem;
   646→    cursor: pointer;
   647→    transition: all 0.2s ease;
   648→    text-align: left;
   649→    width: 100%;
   650→}
   651→
   652→.suggested-item:focus {
   653→    outline: none;
   654→    box-shadow: 0 0 0 3px var(--focus-ring);
   655→}
   656→
   657→.suggested-item:hover {
   658→    background: var(--surface-hover);
   659→    border-color: var(--primary-color);
   660→    color: var(--primary-color);
   661→    transform: translateX(2px);
   662→}
   663→
   664→/* Responsive Design */
   665→@media (max-width: 768px) {
   666→    .main-content {
   667→        flex-direction: column;
   668→    }
   669→    
   670→    .sidebar {
   671→        width: 100%;
   672→        border-right: none;
   673→        border-bottom: 1px solid var(--border-color);
   674→        padding: 1rem;
   675→        order: 2;
   676→        max-height: 40vh;
   677→    }
   678→    
   679→    .sidebar::-webkit-scrollbar {
   680→        width: 8px;
   681→    }
   682→    
   683→    .sidebar::-webkit-scrollbar-track {
   684→        background: var(--surface);
   685→    }
   686→    
   687→    .sidebar::-webkit-scrollbar-thumb {
   688→        background: var(--border-color);
   689→        border-radius: 4px;
   690→    }
   691→    
   692→    .sidebar::-webkit-scrollbar-thumb:hover {
   693→        background: var(--text-secondary);
   694→    }
   695→    
   696→    .chat-main {
   697→        order: 1;
   698→    }
   699→    
   700→    header {
   701→        padding: 1rem;
   702→    }
   703→    
   704→    header h1 {
   705→        font-size: 1.5rem;
   706→    }
   707→    
   708→    .chat-messages {
   709→        padding: 1rem;
   710→    }
   711→    
   712→    .message {
   713→        max-width: 90%;
   714→    }
   715→    
   716→    .chat-input-container {
   717→        padding: 1rem;
   718→        gap: 0.5rem;
   719→    }
   720→    
   721→    #chatInput {
   722→        padding: 0.75rem 1rem;
   723→        font-size: 0.9rem;
   724→    }
   725→    
   726→    #sendButton {
   727→        padding: 0.75rem 1rem;
   728→        min-width: 48px;
   729→    }
   730→    
   731→    .stat-value {
   732→        font-size: 1.25rem;
