     1→// API base URL - use relative path to work from any host
     2→const API_URL = '/api';
     3→
     4→// Global state
     5→let currentSessionId = null;
     6→
     7→// DOM elements
     8→let chatMessages, chatInput, sendButton, totalCourses, courseTitles;
     9→
    10→// Initialize
    11→document.addEventListener('DOMContentLoaded', () => {
    12→    // Get DOM elements after page loads
    13→    chatMessages = document.getElementById('chatMessages');
    14→    chatInput = document.getElementById('chatInput');
    15→    sendButton = document.getElementById('sendButton');
    16→    totalCourses = document.getElementById('totalCourses');
    17→    courseTitles = document.getElementById('courseTitles');
    18→    
    19→    setupEventListeners();
    20→    createNewSession();
    21→    loadCourseStats();
    22→});
    23→
    24→// Event Listeners
    25→function setupEventListeners() {
    26→    // Chat functionality
    27→    sendButton.addEventListener('click', sendMessage);
    28→    chatInput.addEventListener('keypress', (e) => {
    29→        if (e.key === 'Enter') sendMessage();
    30→    });
    31→    
    32→    
    33→    // Suggested questions
    34→    document.querySelectorAll('.suggested-item').forEach(button => {
    35→        button.addEventListener('click', (e) => {
    36→            const question = e.target.getAttribute('data-question');
    37→            chatInput.value = question;
    38→            sendMessage();
    39→        });
    40→    });
    41→}
    42→
    43→
    44→// Chat Functions
    45→async function sendMessage() {
    46→    const query = chatInput.value.trim();
    47→    if (!query) return;
    48→
    49→    // Disable input
    50→    chatInput.value = '';
    51→    chatInput.disabled = true;
    52→    sendButton.disabled = true;
    53→
    54→    // Add user message
    55→    addMessage(query, 'user');
    56→
    57→    // Add loading message - create a unique container for it
    58→    const loadingMessage = createLoadingMessage();
    59→    chatMessages.appendChild(loadingMessage);
    60→    chatMessages.scrollTop = chatMessages.scrollHeight;
    61→
    62→    try {
    63→        const response = await fetch(`${API_URL}/query`, {
    64→            method: 'POST',
    65→            headers: {
    66→                'Content-Type': 'application/json',
    67→            },
    68→            body: JSON.stringify({
    69→                query: query,
    70→                session_id: currentSessionId
    71→            })
    72→        });
    73→
    74→        if (!response.ok) throw new Error('Query failed');
    75→
    76→        const data = await response.json();
    77→        
    78→        // Update session ID if new
    79→        if (!currentSessionId) {
    80→            currentSessionId = data.session_id;
    81→        }
    82→
    83→        // Replace loading message with response
    84→        loadingMessage.remove();
    85→        addMessage(data.answer, 'assistant', data.sources);
    86→
    87→    } catch (error) {
    88→        // Replace loading message with error
    89→        loadingMessage.remove();
    90→        addMessage(`Error: ${error.message}`, 'assistant');
    91→    } finally {
    92→        chatInput.disabled = false;
    93→        sendButton.disabled = false;
    94→        chatInput.focus();
    95→    }
    96→}
    97→
    98→function createLoadingMessage() {
    99→    const messageDiv = document.createElement('div');
   100→    messageDiv.className = 'message assistant';
   101→    messageDiv.innerHTML = `
   102→        <div class="message-content">
   103→            <div class="loading">
   104→                <span></span>
   105→                <span></span>
   106→                <span></span>
   107→            </div>
   108→        </div>
   109→    `;
   110→    return messageDiv;
   111→}
   112→
   113→function addMessage(content, type, sources = null, isWelcome = false) {
   114→    const messageId = Date.now();
   115→    const messageDiv = document.createElement('div');
   116→    messageDiv.className = `message ${type}${isWelcome ? ' welcome-message' : ''}`;
   117→    messageDiv.id = `message-${messageId}`;
   118→    
   119→    // Convert markdown to HTML for assistant messages
   120→    const displayContent = type === 'assistant' ? marked.parse(content) : escapeHtml(content);
   121→    
   122→    let html = `<div class="message-content">${displayContent}</div>`;
   123→    
   124→    if (sources && sources.length > 0) {
   125→        // Format sources - handle both old format (strings) and new format (objects with text/link)
   126→        const formattedSources = sources.map(source => {
   127→            if (typeof source === 'string') {
   128→                // Old format - just return the text
   129→                return source;
   130→            } else if (source && source.text) {
   131→                // New format - create clickable link if available
   132→                if (source.link) {
   133→                    return `<a href="${source.link}" target="_blank" rel="noopener noreferrer">${source.text}</a>`;
   134→                } else {
   135→                    return source.text;
   136→                }
   137→            }
   138→            return source; // fallback
   139→        }).join(', ');
   140→        
   141→        html += `
   142→            <details class="sources-collapsible">
   143→                <summary class="sources-header">Sources</summary>
   144→                <div class="sources-content">${formattedSources}</div>
   145→            </details>
   146→        `;
   147→    }
   148→    
   149→    messageDiv.innerHTML = html;
   150→    chatMessages.appendChild(messageDiv);
   151→    chatMessages.scrollTop = chatMessages.scrollHeight;
   152→    
   153→    return messageId;
   154→}
   155→
   156→// Helper function to escape HTML for user messages
   157→function escapeHtml(text) {
   158→    const div = document.createElement('div');
   159→    div.textContent = text;
   160→    return div.innerHTML;
   161→}
   162→
   163→// Removed removeMessage function - no longer needed since we handle loading differently
   164→
   165→async function createNewSession() {
   166→    currentSessionId = null;
   167→    chatMessages.innerHTML = '';
   168→    addMessage('Welcome to the Course Materials Assistant! I can help you with questions about courses, lessons and specific content. What would you like to know?', 'assistant', null, true);
   169→}
   170→
   171→// Load course statistics
   172→async function loadCourseStats() {
   173→    try {
   174→        console.log('Loading course stats...');
   175→        const response = await fetch(`${API_URL}/courses`);
   176→        if (!response.ok) throw new Error('Failed to load course stats');
   177→        
   178→        const data = await response.json();
